
import {dados} from './dados.min.js';
// Importa os dados das raças de cachorro do arquivo dados.min.js
function resultado() {
    //Função principal que é executada quando o botão "Pesquisar" é clicado
       // console.log("Clicou") // mensagem para confirmar que a função foi chamada
    //Obtém a seção onde os resultados serão exibidos
    let section = document.getElementById("resultados-pesquisa");

    let campoPesquisa = document.getElementById("campo-pesquisa").value

    if (!campoPesquisa){
        section.innerHTML = "<p>Digite uma raça para realizar a pesquisa!</p>"
        return
    }
    campoPesquisa = campoPesquisa.toLowerCase()
    //Inicializa uma string vazia para armazenar os resultados da pesquisa
    let resultados = "";
    let titulo = "";
    let descricao = "";
    let tamanho = "";
    let temperamento = "";
    let cuidados = "";
    let tags = "";
    //Itera sobre cada raça de cachorro nos dados importados    
    for (let dado of dados) {
        titulo = dado.titulo.toLowerCase();
        descricao = dado.descricao.toLowerCase();
        tamanho = dado.tamanho.toLowerCase();
        temperamento = dado.temperamento.toLowerCase();
        cuidados = dado.cuidados.toLowerCase();
        tags = dado.tags.toLowerCase();

        if (titulo.includes(campoPesquisa) || descricao.includes(campoPesquisa) || tamanho.includes(campoPesquisa) || temperamento.includes(campoPesquisa) || cuidados.includes(campoPesquisa) || tags.includes(campoPesquisa)) {
            //Cria um novo elemento div para cada raça e adiciona as informações relevantes  
        resultados += `
        <div class="item-resultado">
            <h2>
                <a href="#" target="_blank">${dado.titulo}</a>
            </h2>
            <p class="Descrição">${dado.descricao}</p>
            <p class="Porte">${dado.tamanho}</p>
            <p class="Temperamento">${dado.temperamento}</p>
            <p class="Cuidados">${dado.cuidados}</p>
            <a href=${dado.link} target="_blank">Mais informações</a>
        </div>
    `;
        }
        
    }
    if (!resultados) {
        resultados = "<p>Informação não encontrada!</p>"
    }
     // Atualiza o conteúdo da seção com os resultados da pesquisa   
    section.innerHTML = resultados  
    
}

window.onload = () => {
// Quando a página é carregada, adiciona um ouvinte de evento ao botão de pesquisa
    const botaoPesquisar = document.getElementById('resultado');
    botaoPesquisar.addEventListener('click', resultado);
  };
